export const CACHE_TTL_ONE_MINUTE = 60; // 1 minute in seconds
export const CACHE_TTL_ONE_HOUR = 60 * CACHE_TTL_ONE_MINUTE; // 1 hour in seconds
export const CACHE_TTL_ONE_DAY = 24 * CACHE_TTL_ONE_HOUR; // 24 hours in seconds
export const CACHE_TTL_ONE_WEEK = 7 * CACHE_TTL_ONE_DAY; // 7 days in seconds
export const CACHE_TTL_FOUR_WEEKS = 4 * CACHE_TTL_ONE_WEEK; // 4 weeks in seconds
